package a01_diexp.z01_vo;
// a01_diexp.z01_vo.Food
public class Food {
	private String name;
	private int price;
	public Food() {
		// TODO Auto-generated constructor stub
	}
	public Food(String name, int price) {
		this.name = name;
		this.price = price;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getPrice() {
		return price;
	}
	public void setPrice(int price) {
		this.price = price;
	}
	
}
